document.addEventListener("DOMContentLoaded", function () {
	function a() {
		b.classList.remove("menu--showed");
		c.style.overflow = "initial";
		document.querySelector(".header__close").style.display = "none";
		document.querySelector(".header__nav").classList.remove("nav--showed")
	}
	var e = document.querySelector(".js-burger"),
		f = document.querySelector(".js-close"),
		b = document.querySelector(".menu-toggle"),
		d = document.querySelector(".header__nav"),
		c = document.body;
	d.addEventListener("click", a);
	e.addEventListener("click", function () {
		b.classList.add("menu--showed");
		c.style.overflow = "hidden";
		d.classList.add("nav--showed");
		document.querySelector(".header__close").style.display = "block";
		document.addEventListener("keydown", function (g) {
			27 == g.keyCode && a()
		})
	});
	f.addEventListener("click", a)
});
