document.addEventListener("DOMContentLoaded", function () {
	function o() {
		t.classList.add("menu--showed");
		r.style.overflow = "hidden";
		i.classList.add("nav--showed");
		document.querySelector(".header__close").style.display = "block";
		document.addEventListener("keydown", function (t) {
			t.keyCode == e.ESC && n()
		})
	}

	function n() {
		t.classList.remove("menu--showed");
		r.style.overflow = "initial";
		document.querySelector(".header__close").style.display = "none";
		document.querySelector(".header__nav").classList.remove("nav--showed")
	}
	const u = document.querySelector(".js-burger"),
		f = document.querySelector(".js-close"),
		t = document.querySelector(".menu-toggle"),
		i = document.querySelector(".header__nav"),
		r = document.body;
	let e = {
		ESC: 27
	};
	i.addEventListener("click", n);
	u.addEventListener("click", o);
	f.addEventListener("click", n)
})
